parameters.css:
*::-webkit-scrollbar {
	width: 6px;
	height: 6px;
	background-color:var(--cw);
	display:none;
}
*::-webkit-scrollbar-thumb {
	background: var(--cb);
	border-radius:3px;
	display:none;
}
@keyframes bg-scrolling {100%{background-position:-128px 128px;}}
.bg_anim{
	background: linear-gradient(-35deg, #5d9bd5 10%, #dbb5ff 75%) repeat 0 0;
	width:100%;
	height:100vh;
}

.bg_anim:after{
	content:'';
	position:absolute;
	background: url("data:image/svg+xml,%3csvg width='128' height='128' viewBox='0 0 64 64' fill='none' xmlns='http://www.w3.org/2000/svg'%3e%3cg opacity='0.1'%3e%3cpath d='M42 64H64L0 0V22L42 64Z M42 0L64 22L64 0H42Z' fill='%23FFF'/%3e%3c/g%3e%3cg opacity='0.2'%3e%3cpath d='M22 64L42 64L0 22L-4e-06 22.5V42L22 64Z M22 0L64 42V22L42 0H22Z' fill='%23FFF'/%3e%3c/g%3e%3c/svg%3e") repeat 0 0;
	left:0px;
	top:0px;
	width:100%;
	height:100vh;
	background-repeat: repeat;
	animation:bg-scrolling 8s infinite;
	animation-timing-function:linear;
}
.main_bottom{
	box-shadow:0px 8px 12px rgba(161,194,250,0.5);
	background-color:rgba(161,194,250,0.5);
	transition-duration:0.6s;
	border-radius:16px;
	backdrop-filter: blur(6px);
	border:2px solid rgba(255,255,255,0.5);
	cursor:pointer;
}
.main_bottom:hover{
	box-shadow:0px 8px 12px rgba(255,255,255,0.4);
	background-color:rgba(255,255,255,0.4);
	transition-duration:0.2s;
}
#code_edit{
	width:60%;
	height:60%;
	display:grid;
	grid-auto-rows: min-content minmax(calc(100% - 200px),128px) min-content auto ;
	background-color:rgba(115,138,201,0.75);
	box-sizing:border-box;
	overflow: hidden;
	border-radius:16px;
	box-shadow:0px 8px 12px rgba(161,194,250,0.5);
	position: absolute;
	bottom:0px;
	padding:0px;
	margin:0 0 3% 24px;
	border:2px solid rgba(255,255,255,0.5);
	backdrop-filter: blur(8px);
}
#code_edit *{box-sizing:border-box;font-size:16px;font-family:monospace,Arial;}
.codeView{
    overflow: scroll;
    position: relative;
    width: 100%
    height: max-content;
    border-bottom:2px solid rgba(255,255,255,0.5);
}
#console{
    width: 100%;
    height: max-content;
    display:grid;
    color:#fff;
    align-items:start;
    box-sizing:border-box;
    padding:10px;
}
.label{
    padding:10px 4px;
    margin: 0 8px;
    border-bottom:2px solid rgba(255,255,255,0.5);
    color:rgba(255,255,255,0.5);
}
.bottoms_panel{
    display:grid;
    grid-template-columns:33% 33% 33%;
    justify-content: center; align-items: center;
    grid-gap:8px;
    padding: 0 8px;
}
.code_bottom{
    width: 74px;
    height: 30px;
    margin-bottom:6px;
    display: flex;
    color:#fff;
    justify-content: center; align-items: center;
    transition-duration:0.6s;
    border-radius:0 0 16px 16px;
    border:2px solid rgba(255,255,255,0.5);
    border-top:none;
    cursor:pointer;
}
.code_bottom:hover{
	background-color:rgba(255,255,255,0.4);
	transition-duration:0.2s;

}
.code_text,.code_show{
	background-color:transparent;
	font-family:monospace;
	text-decoration:none;
	position:absolute;
	overflow:hidden;
	outline:none;
	resize:none;
	padding:10px;
	tab-size:4;
	margin:0;
}
.code_text{
	min-width:100%;
	min-height:100%;
	border:none;
	color:transparent;
	caret-color:#fff;
	user-select: text;
}
.code_show{
	padding-top:9px;
	padding-left:12px;
	left:-2px;top:0.8px;color:#fff;
}
.code_elem_1{color:#aaa;font-style:italic;}
.code_elem_2{color:rgba(255,255,255,0.5);}
.code_elem_3{color:#edccff;}
.code_elem_4{color:#b0ff00;}
.code_elem_5{color:#ffb399;}

